import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import Skeleton from "react-loading-skeleton";

export default function Products() {
  const [data, setData] = useState([]);
  const [filter, setFilter] = useState(data);
  const [loading, setLoading] = useState(false);
  let componentMounted = true;

  useEffect(() => {
    const getProducts = async () => {
      setLoading(true);

      if (componentMounted) {
        const response = await fetch("https://fakestoreapi.com/products");
        console.log("Response: ",response);
        setData(await response.clone().json()); 
        console.log("Data: ",data);
        setFilter(data.filter((x) => x.category === "jewelery"));
        console.log("Data: ",data);
        setLoading(false);
        console.log("Filter: ",filter);
      }
      return () => {
        componentMounted = false;
      }; 
    };
    getProducts();

  }, []);

  const Loading = () => {
    return (
      <>
        <div className="col-md-3">
          <Skeleton height={350} />
        </div>

        <div className="col-md-3">
          <Skeleton height={350} />
        </div>

        <div className="col-md-3">
          <Skeleton height={350} />
        </div>
      </>
    );
  };
  const filterProduct = (cat) => {
    const updatedList = data.filter((x) => x.category === cat);
    setFilter(updatedList);
  };

  const ShowProducts = () => {

    return (
      <>
        <div className="buttons d-flex justify-content-center mb-5 pb-5">
          <button
            className="btn btn-outline-dark me-2"
            onClick={() => filterProduct("jewelery")}
          >
            All
          </button>
          <button
            className="btn btn-outline-dark me-2"
            onClick={() => filterProduct("jewelery")}
          >
            Jewelery
          </button>
        </div>
        {
        filter.map((product) => {
          return (
            <>
              <div className="col-md-3 mb-4"  key={product.id}>
                <div className="card h-100 text-center p-4" >
                  <img
                    src={product.image}
                    height="250px"
                    className="card-img-top"
                    alt={product.title}
                    aria-label={product.title}
                  />
                  <div className="card-body">
                    <h5 className="card-title mb-0">
                      {product.title.substring(0, 12)}...
                    </h5>
                    <p className="card-text lead fw-bold" aria-describedby={`description-${product.id}`}>
                      ${product.price}
                    </p>
                    <NavLink
                      to={`/products/${product.id}`}
                      className="btn  btn-outline-dark"
                      aria-describedby={`description-${product.id}`}
                    >
                      Ver detalles
                    </NavLink>
                    <div id={`description-${product.id}`} className="visually-hidden">
                      {product.title} - ${product.price}
                    </div>
                  </div>
                </div>
              </div>


            </>
          );
        })}

      </>
    );
  };

  return (
    <>
      <div className="container my-5 py-5">
        <div className="row">
          <div className="col-12 mb-5">
            <h1 className="display-6 fw-bolder text-center">Joyería disponible</h1>
            <hr />
          </div>
        </div>
        <div className="row justify-content-center">
          {loading ? <Loading /> : <ShowProducts />}
        </div>
      </div>
    </>
  );
}
